        public static void SeedDatabase<TDbContext>(this TDbContext dbContext) where TDbContext : NorthwindDbContext
        {
            if (dbContext == null)
            {
                return;
            }

            try
            {
                dbContext.Database.EnsureCreated();
                SeedSellers(dbContext);
                SeedCategories(dbContext);
                SeedProducts(dbContext);
                SeedCustomers(dbContext);
                SeedOrders(dbContext);
                SeedOrderDetails(dbContext);
                SeedShippers(dbContext);
                SeedSuppliers(dbContext);
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex.Message);
            }
        }

